import { Button, Popconfirm, Tooltip } from "antd";
import React, { useState } from "react";

import { DeleteOutlined } from "@ant-design/icons";

function RowDelete({ personelRow, tableData }) {
  const [myTableData, setMyTableData] = useState(tableData);

  const handleDelete = (personelRow) => {
    const newData = [...myTableData];
    const index = newData.findIndex((item) => personelRow.key === item.key);
    if (index > -1) {
      newData.splice(index, 1);
      setMyTableData(newData);
    }
  };
  return (
    <>
      <Popconfirm
        title="Silmek İStediğinize Emin misiniz?"
        onConfirm={() => handleDelete(personelRow)}
      >
        <Tooltip title="SİL">
          <Button
            style={{ margin: "5px", background: "#F52441" }}
            icon={<DeleteOutlined />}
          />
        </Tooltip>
      </Popconfirm>
    </>
  );
}

export default RowDelete;
