import { Button, Form, Input, Tooltip } from 'antd';
import React from 'react'
import { SyncOutlined } from "@ant-design/icons";

function UpdateCrud({ prop }) {

    const dataUpdate = (object) => {
        const selectDataUpdate = {
            adsoyad: object.adi, 
            ebysno: object.ebys,
        }; 
        confirm({
            title: "Güncelleme",
            icon: <SyncOutlined />,
            content: (
                <Form
                name="basic"
                labelCol={{ span: 5 }}
                wrapperCol={{ span: 19 }}
                labelAlign="left"
                initialValues={{
                    adsoyad: object.adi,
                    ebysno: object.ebys,
                }}                
                >
                    <strong>
                    <Form.Item label="AD - SOYAD" name="adsoyad">
                        <Input
                        onChange={(e) => {
                            selectDataUpdate.adsoyad = e.target.value; 
                        }}
                        value={selectDataUpdate.adsoyad}
                        />
                    </Form.Item>
                    </strong>

                </Form>
            )
        })
    }
  return (
    <>
      <Tooltip>
        <Button
        onClick={() => dataUpdate(prop)}
        icon={<SyncOutlined />}
        />

      </Tooltip>
    </>
  )
}

export default UpdateCrud;
