//       setMyTableData(newData);
//     }
//   };
//   console.log(myTableData);
  return (
    <>
      <Popconfirm title="Silmek İStediğinize Emin misiniz?">
        <Tooltip title="SİL">
          <Button
            style={{ margin: "5px", background: "#F52441" }}
            icon={<DeleteOutlined />}
            onClick={() => handleDeleteRow(record)}
          >
            SİL
          </Button>
        </Tooltip>
      </Popconfirm>
    </>
  );
}
export default RowDelete;
