import React from "react";
import { Layout, Menu } from "antd";
import {
  UserOutlined,
  ShopOutlined,
  LogoutOutlined,
  NotificationOutlined,
  FormOutlined,
  ShoppingOutlined,
  InteractionOutlined,
  UserAddOutlined, 
  UsergroupDeleteOutlined,
} from "@ant-design/icons";
import { NavLink } from "react-router-dom";

function Sidebar() {
  const { currentTheme } = useThemeSwitcher();
  console.log(currentTheme);
  const { Sider } = Layout;
  const getItem = (label, key, icon, children, type, link) => ({
    key,
    icon,
    children,
    label: <NavLink to={link}>{label}</NavLink>,
    type,
    link,
  });
  const items = [
    getItem("İdari Büro", "1", <UserOutlined />, [
      getItem(
        "İdari Büro Duyuru",
        "duyuru",
        <NotificationOutlined />,
        null,
        null,
        "/idariburoduyuru"
      ),
      getItem(
        "İdari Büro Nöbetler",
        "nobet",
        <FormOutlined />,
        null,
        null,
        "/idariburonobetler"
      ),
    ]),

    getItem("Personeller", "2", <UserOutlined />, [
      getItem("Personel Ekle", "ekle", <UserAddOutlined />, null, null, '/personelekle'),
      getItem("Personel Göster", "goster", <UsergroupDeleteOutlined />, null, null, '/personelgoster'),
    ]),

    getItem("Ürünler", "3", <ShopOutlined />, [
      getItem(
        "Ürünleri Listele",
        "listele",
        <ShoppingOutlined />,
        null,
        null,
        "/urunlistele"
      ),
      getItem(
        "Ürünleri Güncelle",
        "guncelle",
        <InteractionOutlined />,
        null,
        null,
        "/urunguncelle"
      ),
    ]),

    getItem("Çıkış Yap", "4", <LogoutOutlined />, null, null, '/logout'),
  ];
  return (
    <>
      <Sider trigger={null} collapsible>
        <NavLink to="/">
          <p className="SiderLogo">PAUL SAWYER</p>
        </NavLink>
        <div className="demo-logo-vertical" />
        <Menu
          className="menu"
          theme="dark"
          mode="inline"
          defaultSelectedKeys={["1"]}
          items={items}
        />
              <div className="darkButton">
        {currentTheme}
        <SwitchButton />
      </div>
      </Sider>
    </>
  );
}

export default Sidebar;
