import { Button, Form, Input, Modal, Tooltip } from "antd";
import React from "react";
import { SyncOutlined } from "@ant-design/icons";

const { confirm } = Modal; 

function UpdateCrud({ prop, tableData, setTableData }) {

  const dataUpdate = (object) => {
    const selectDataUpdate = {
      adsoyad: object.adi,
      ebysno: object.ebys,
    };
    confirm({
      title: "Güncelleme",
      icon: <SyncOutlined />,
      content: (
        <Form
          name="basic"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 19 }}
          labelAlign="left"
          initialValues={{
            adsoyad: object.adi,
            ebysno: object.ebys,
          }}
        >
          <strong>
            <Form.Item label="AD - SOYAD" name="adsoyad">
              <Input
                onChange={(e) => {
                  selectDataUpdate.adsoyad = e.target.value;
                }}
                value={selectDataUpdate.adsoyad}
              />
            </Form.Item>
          </strong>
          <strong>
            <Form.Item label="EBYS NO" name="ebysno">
              <Input
                onChange={(e) => {
                  selectDataUpdate.ebysno = e.target.value;
                }}
                value={selectDataUpdate.ebysno}
              />
            </Form.Item>
          </strong>
        </Form>
      ),
      onOk() {
        const newTableData = tableData.map((tableData) => {
            if (tableData.key === object.key) {
                const newData = {
                    ...tableData,
                    adi: selectDataUpdate.adsoyad,
                    ebys: selectDataUpdate.ebysno,
                }; 
                return newData; 
            } else {
                return tableData; 
            }
        });
        setTableData(newTableData);
      }, 
      closable: true,
      width:600,
    });
  };

  return (
    <>
      <Tooltip>
        <Button onClick={() => dataUpdate(prop)} icon={<SyncOutlined />} style={{ background:"#3385FF"}} />
      </Tooltip>
    </>
  );
}

export default UpdateCrud;
