import { Button, Col, Popconfirm, Row, Tooltip } from "antd";
import React, { useState } from "react";
import Forms from "./forms";
import Tables from "../../components/DataTables/tables";
import { DeleteOutlined, EyeOutlined, SyncOutlined } from "@ant-design/icons";
import ModalView from "../../components/View/ModalView";

function PersonelEkle({prop, setSelectedRowData}) {
  const [isModalOpen, setIsModalOpen] = useState();
  const showModal = () => {
    setIsModalOpen(true);
    setSelectedRowData(); 
  };
  const [tableData, setTableData] = useState([
    {
      key: "1",
      adi: "Volkan",
      soyadi: "ÖZBEK",
      sicil: "495412",
      sube: "Adli Bilişim",
      buro: "Veri İnceleme",
    },
    {
      key: "2",
      adi: "Sinan",
      soyadi: "AKDAL",
      sicil: "454354",
      sube: "Önleme",
      buro: "Sanal Devriye",
    },
    {
      key: "3",
      adi: "Zeynep",
      soyadi: "ŞENTÜRK",
      sicil: "412568",
      sube: "Önleme",
      buro: "Sanal Devriye",
    },
    {
      key: "4",
      adi: "Mehmet Nur",
      soyadi: "DEVECİOĞLU",
      sicil: "521836",
      sube: "Bilgi Teknolojileri",
      buro: "Yazılım ve Veritabanı",
    },
  ]);
  const columns = [
    {
      title: "Adı",
      dataIndex: "adi",
    },
    {
      title: "Soyadı",
      dataIndex: "soyadi",
    },
    {
      title: "Sicil",
      dataIndex: "sicil",
    },
    {
      title: "Şube Adı",
      dataIndex: "sube",
    },
    {
      title: "Büro Adı",
      dataIndex: "buro",
    },
    {
      title: "İşlemler",
      dataIndex: "islemler",
      width: "15%",
      render: (_, record) => (
        <>
          <Tooltip title="GÖRÜNTÜLE">
            <Button
              style={{ margin: "5px", background: "#009D7E" }}
              icon={<EyeOutlined />}
              onClick={() => showModal(prop)}
            />
            <ModalView />
          </Tooltip>

          <Tooltip title="GÜNCELLE">
            <Button
              style={{ margin: "5px", background: "#3385FF" }}
              icon={<SyncOutlined />}
            />
          </Tooltip>

          <Popconfirm title="Silmek İstediğinize Emin misiniz?">
            <Tooltip title="SİL">
              <Button
                style={{ margin: "5px", background: "#F52441" }}
                icon={<DeleteOutlined />}
              />
            </Tooltip>
          </Popconfirm>
        </>
      ),
    },
  ];
  const updateTableData = (newData) => {
    setTableData((newTableData) => [newData, ...newTableData]);
  };
  console.log(tableData);

  return (
    <>
      <Row>
        <Col span={24}>
          <Forms setTableData={updateTableData} />
        </Col>
        <Col span={24}>
          <Tables columns={columns} tableData={tableData} />
        </Col>
      </Row>
    </>
  );
}

export default PersonelEkle;
