import { Button, Form, Input, Modal, Tooltip } from "antd";
import React from "react";
import { SyncOutlined } from "@ant-design/icons";



const { confirm } = Modal; 

function UpdateCrud({ prop }) {
  const dataUpdate = (object) => {
    const selectDataUpdate = {
      adsoyad: object.adi,
      ebysno: object.ebys,
    };
    confirm({
      title: "Güncelleme",
      icon: <SyncOutlined />,
      content: (
        <Form
          name="basic"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 19 }}
          labelAlign="left"
          initialValues={{
            adsoyad: object.adi,
            ebysno: object.ebys,
          }}
        >
          <strong>
            <Form.Item label="AD - SOYAD" name="adsoyad">
              <Input
                onChange={(e) => {
                  selectDataUpdate.adsoyad = e.target.value;
                }}
                value={selectDataUpdate.adsoyad}
              />
            </Form.Item>
          </strong>
          <strong>
            <Form.Item label="EBYS NO" name="ebysno">
              <Input
                onChange={(e) => {
                  selectDataUpdate.ebysno = e.target.value;
                }}
                value={selectDataUpdate.ebysno}
              />
            </Form.Item>
          </strong>
        </Form>
      ),
      onOk() {

      }
    });
  };
  return (
    <>
      <Tooltip>
        <Button onClick={() => dataUpdate(prop)} icon={<SyncOutlined />} />
      </Tooltip>
    </>
  );
}

export default UpdateCrud;
