import { Button, Divider, Dropdown, Popover, Space } from "antd";
import React from "react";
import { NavLink } from "react-router-dom";
import {
  DownOutlined,
  LogoutOutlined,
  AppstoreOutlined,
} from "@ant-design/icons";
import SwitchButton from "../components/DarkModeButton/SwitchButton";

function Header() {
  const getItem = (key, icon, children, label, type, className, link) => ({
    key,
    icon,
    children,
    label: <NavLink to={link}>{label}</NavLink>,
    className,
    type,
    link,
  });
  const items = [
    getItem(
      "logout",
      <LogoutOutlined />,
      null,
      "Çıkış Yap",
      null,
      null,
      "/logout"
    ),
  ];
  const menuProps = {
    items,
  };

  const text = (
    <span>SİBER SUÇLARLA MÜCADELE
      <Divider />
    </span>
  ); 

  const content = (
    <img src="./assets/images/siber.png" alt="siber" style={{ width:'70px', height:'70px', borderRadius:'50px'}}/>
  )
  return (
    <>
      <span className="header" style={{ fontWeight: "bold" }}>
        PAUL SAWYER
      </span>
      <div className="darkButton">
        <SwitchButton />
      </div>
      <Popover
        placement="bottom"
        title={text}
        content={content}
        trigger="click"
        style={{ width: "40px", height: "40px" }}
      >
        <AppstoreOutlined
          style={{
            position: "absolute",
            right: "17%",
            top: "22px",
            fontSize: "24px",
          }}
        />
      </Popover>

      <Dropdown menu={menuProps} items={items} className="rightButton">
        <Button>
          <NavLink to="/logout"></NavLink>
          <Space>
            MEHMET NUR DEVECİOĞLU
            <DownOutlined />
          </Space>
        </Button>
      </Dropdown>
    </>
  );
}

export default Header;
