import { Button, Dropdown, Space } from "antd";
import React from "react";
import { NavLink } from "react-router-dom";
import { DownOutlined, LogoutOutlined } from "@ant-design/icons";
import { useThemeSwitcher } from "react-css-theme-switcher";
import SwitchButton from "../components/SwitchButton";

function Header() {
  const { currentTheme } = useThemeSwitcher();
  console.log(currentTheme);

  const getItem = (key, icon, children, label, type, className, link) => ({
    key,
    icon,
    children,
    label: <NavLink to={link}>{label}</NavLink>,
    className,
    type,
    link,
  });
  const items = [
    getItem(
      "logout",
      <LogoutOutlined />,
      null,
      "Çıkış Yap",
      null,
      null,
      "/logout"
    ),
  ];
  const menuProps = {
    items,
  };
  return (
    <>
      <span className="header" style={{ fontWeight: "bold" }}>
        PAUL SAWYER
      </span>
      <div className="darkButton">
        DÜĞMEYE BASINIZ {currentTheme}
        <SwitchButton />
      </div>

      <Dropdown menu={menuProps} items={items} className="rightButton">
        <Button>
          <NavLink to="/logout"></NavLink>
          <Space>
            MEHMET NUR DEVECİOĞLU
            <DownOutlined />
          </Space>
        </Button>
      </Dropdown>
    </>
  );
}

export default Header;
