import {
  Button,
  Col,
  Dropdown,
  Popover,
  Row,
  Space,
  Tooltip,
  Layout,
} from "antd";
import React from "react";
import { NavLink } from "react-router-dom";
import {
  DownOutlined,
  LogoutOutlined,
  AppstoreOutlined,
} from "@ant-design/icons";
import SwitchButton from "../components/DarkModeButton/SwitchButton";
import { useThemeSwitcher } from "react-css-theme-switcher";

function Header() {
  const { currentTheme } = useThemeSwitcher();
  const getItem = (key, icon, children, label, type, className, link) => ({
    key,
    icon,
    children,
    label: <NavLink to={link}>{label}</NavLink>,
    className,
    type,
    link,
  });
  const items = [
    getItem(
      "logout",
      <LogoutOutlined />,
      null,
      "Çıkış Yap",
      null,
      null,
      "/logout"
    ),
  ];
  const menuProps = {
    items,
  };

  const text = <span>SİBER SUÇLARLA MÜCADELE</span>;

  const content = (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <Row gutter={12}>
        <Col span={8}>
          <NavLink to={"https://www.egm.gov.tr/siber"}>
            <img
              src="./assets/images/siber.png"
              alt="siber"
              style={{ width: "70px", height: "70px", borderRadius: "50px" }}
            />
          </NavLink>
        </Col>
        <Col span={8}>
          <NavLink to={"https://www.egm.gov.tr/siber"}>
            <img
              src="./assets/images/siber.png"
              alt="siber"
              style={{ width: "70px", height: "70px", borderRadius: "50px" }}
            />
          </NavLink>
        </Col>
        <Col span={8}>
          <NavLink to={"https://www.egm.gov.tr/siber"}>
            <img
              src="./assets/images/siber.png"
              alt="siber"
              style={{ width: "70px", height: "70px", borderRadius: "50px" }}
            />
          </NavLink>
        </Col>
      </Row>
    </div>
  );
  return (
    <></>
