    //     <Col span={12}>
    //       <span style={{ fontWeight: "bold", color: "white", display:'flex', justifyContent:'center' }}>
    //         PAUL SAWYER
    //       </span>
    //     </Col>
    //     <Col className="darkButton">
    //       <SwitchButton />
    //     </Col>
       
    //       <Popover
    //         placement="bottom"
    //         title={text}
    //         content={content}
    //         trigger="click"
