    //         <AppstoreOutlined
    //           style={{
    //             position: "absolute",
    //             right: "17%",
    //             top: "25px",
    //             fontSize: "24px",
    //           }}
    //         />
    //       </Popover>
     
    //     <Col>
    //       <Dropdown menu={menuProps} items={items} className="rightButton">
    //         <Button>
    //           <NavLink to="/logout">
    //             <Tooltip placement="bottomRight" title="Çıkış Yap">
    //             </Tooltip>
    //           </NavLink>
    //           <Space>
    //             MEHMET NUR DEVECİOĞLU
    //             <DownOutlined />
    //           </Space>
    //         </Button>
    //       </Dropdown>
    //     </Col>
    //   </Row>
    // </Layout.Header>

  );
}

export default Header;
