import { Col, Row, Tooltip } from 'antd';
import React, {useState} from 'react'
import Forms from "./forms";
import Tables from "../../components/DataTables/tables";

function PersonelEkle() {
  const [tableData, setTableData] = useState([
    {
      key: "1",
      adi: "Volkan",
      soyadi: "ÖZBEK",
      sicil: "495412",
      sube: "Adli Bilişim",
      buro: "Veri İnceleme", 
    }, 
    {
      key: "2",
      adi: "Sinan",
      soyadi: "AKDAL",
      sicil: "454354",
      sube: "Önleme",
      buro: "Sanal Devriye",
    }, 
    {
      key: "3",
      adi: "Zeynep",
      soyadi: "ŞENTÜRK",
      sicil: "412568",
      sube: "Önleme",
      buro: "Sanal Devriye",
    },
    {
      key: "4",
      adi: "Mehmet Nur",
      soyadi: "DEVECİOĞLU",
      sicil: "521836",
      sube: "Bilgi Teknolojileri",
      buro: "Yazılım ve Veritabanı",
    },
  ]);
  const columns = [
    {
      title: "Adı",
      dataIndex: "adi",
    },
    {
      title: "Soyadı",
      dataIndex: "soyadi",
    },
    {
      title: "Sicil",
      dataIndex: "sicil",
    },
    {
      title: "Şube Adı",
      dataIndex: "sube",
    },
    {
      title: "Büro Adı",
      dataIndex: "buro",
    }, 
    {
      title: "İşlemler",
      dataIndex: "islemler",
      render: (_, record) => (
        <Popconfirm title="Silmek İstediğinize Emin misiniz?">
          <Tooltip title="SİL">
            <Button style={{ margin: "5px", background: "#F52441"}}
            icon={<DeleteOutlined />}
            />
          </Tooltip>
        </Popconfirm>
      )
    }
  ];
  const updateTableData = (newData) => {
    setTableData((newTableData) => [newData, ...newTableData]);
  };
  console.log(tableData);

  return (
    <>
      <Row>
        <Col span={24}>
          <Forms setTableData={updateTableData} />
        </Col>
        <Col span={24}>
          <Tables columns={columns} tableData={tableData} />
        </Col>
      </Row>
    </>
  )
}

export default PersonelEkle;
